mapper.update:
update users set userid=userid

    ,username =#{username}


    ,phone = #{phone}


    ,email=#{email}


    ,password=#{password}


    ,admin=#{admin}

where userid=#{userid}

mapper.select:
select * from users where 1

    and userid=#{userid}


    and username like concat('%',#{username},'%')


    and phone like concat('%',#{phone},'%')


    and email like concat('%',#{email},'%')


    and password=#{password}


    and admin=#{admin}
