mapper.select:
select * from questions where 1

    and qid=#{qid}


    and qmajor like concat('%',#{qmajor},'%')


    and qtype=#{qtype}


    and qcontent like concat('%',#{qcontent},'%')

mapper.update:
update questions set qid=qid

    ,qmajor =#{qmajor}


    ,qtype = #{qtype}


    ,qcontent=#{qcontent}


    ,qanswer=#{qanswer}


    ,qimg=#{qimg}

where qid=#{qid}
